import React,{Component} from 'react';
import logo from './logo.svg';
import './App.css';


// function Hello (props) {
//   return <h2>{props.title}</h2>
// }

// const Hello = (props) =><h2>{props.title}</h2>

class Hello extends Component{
  render(){
  return <h2>{this.props.title}</h2>
  }
}
class Text extends Component{
  render() {
    const textoSegunBool=this.props.boolean ? 'cierto' : 'Falso'
    return(
    <div>
     <p>{this.props.text}</p>
      <p>{this.props.number}</p>
      <p>{textoSegunBool}</p>

    </div>
    )
  }

}
class App extends Component {
  render() {
    return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <Hello title= "Dios te bendiga Yenny"/>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <h3>Vamos a aprender</h3>
        <p>Hola mundo, estoy usando React</p>
        <strong>Esto es un strong</strong>
      </header>
      <Text number={2} text='Texto en string' isActivated />
    </div>

  );
}
}

export default App;
